#! /usr/bin/env ruby
require 'pry'


class String

  def solve
    n = 14
    chars.each_cons(n).with_index do |cons, index|
      return index + n if cons.uniq == cons
    end
  end
end

input = <<~INPUT
INPUT

def main(input)
  input.solve
end

input = DATA.read
